// 给定一个整数数组 A，以及一个整数 target 作为目标值，返回满足 i < j < k 且 A[i] + A[j] + A[k] == target 的元组 i, j, k 的数量。

// 由于结果会非常大，请返回 结果除以 10^9 + 7 的余数。

// 来源：力扣（LeetCode）
// 链接：https://leetcode-cn.com/problems/3sum-with-multiplicity
// 著作权归领扣网络所有。商业转载请联系官方授权，非商业转载请注明出处。

//转为求两数目之和
func threeSumMulti(A []int, target int) int {
	cnt := len(A)
	if cnt < 3 {
		return 0
	}

	sort.Ints(A)
	num := 0
	mod := 1000000007

	for i := 0; i < cnt-1; i++ {
		tmpTarget := target - A[i]
		if tmpTarget < 0 {
			continue
		}

		if A[i]+A[i+1] > tmpTarget && A[cnt-2]+A[cnt-1] < tmpTarget {
			continue
		}

		left := i + 1
		right := cnt - 1

		for left < right {

			if left == i {
				left++
				continue
			}

			if right == i {
				right--
				continue
			}

			switch sum := A[left] + A[right]; {
			case sum == tmpTarget:

				//两端相同，中间的肯定也相同 ,从里面选2两个出来
				if A[left] == A[right] {

					num += (right - left + 1) * (right - left) / 2
					left = right
				} else { //两端不相同

					leftNum := 0 //left相同的个数
					leftVal := A[left]

					rightNum := 0 //right 相同的个数
					rightVal := A[right]
					for left < right && A[left] == leftVal {
						leftNum++
						left++
					}
					left-- //这里要回退以下  重要 关键
					for left < right && A[right] == rightVal {
						rightNum++
						right--
					}

					// fmt.Println(num)
					num += (leftNum * rightNum)

				}

				num = num % mod
			case sum > tmpTarget:
				right--
			case sum < tmpTarget:
				left++

			}

		}

	}

	return num
}
